class Solution {
public:
    int partitionDisjoint(vector<int>& nums) {
        int N = nums.size();
        int maxLeft[N];
        int minRight[N];

        maxLeft[0] = nums[0];
        minRight[N - 1] = nums[N - 1];

        for (int i = 1; i < N; ++i) {
            maxLeft[i] = max(maxLeft[i - 1], nums[i]);
        }

        for (int i = N - 2; i >= 0; --i) {
            minRight[i] = min(minRight[i + 1], nums[i]);
        }

        for (int i = 1; i < N; ++i) {
            if (maxLeft[i - 1] <= minRight[i]) {
                return i;
            } 
        }
        // In case there is no solution, we'll return -1
        return -1;
    }
};

class Solution {
public:
    int partitionDisjoint(vector<int>& nums) {
        int N = nums.size();
        int minRight[N];
        minRight[N - 1] = nums[N - 1];

        for (int i = N - 2; i >= 0; --i) {
            minRight[i] = min(minRight[i + 1], nums[i]);
        }

        int currMax = nums[0];
        for (int i = 1; i < N; ++i) {
            currMax = max(currMax, nums[i - 1]);
            if (currMax <= minRight[i]) {
                return i;
            } 
        }
        // In case there is no solution, we'll return -1
        return -1;
    }
};

class Solution {
public:
    int partitionDisjoint(vector<int>& nums) {
        int currMax = nums[0];
        int possibleMax = nums[0];
        int length = 1;

        for (int i = 1; i < nums.size(); ++i) {
            if (nums[i] < currMax) {
                length = i + 1;
                currMax = possibleMax;
            } else {
                possibleMax = max(possibleMax, nums[i]);
            }
        }

        return length;
    }
};

